        <Responses
          allValuesData={allValuesData}
          setIsLoading={setIsLoading}
          isLoading={isLoading}

        />
        <span ref={messagesEndRef}></span>
      </main>
      <Form
        usersPromptData={usersPromptData}
        updateUsersPrompt={updateUsersPrompt}
        updateValuesData={updateValuesData}
        allValuesData={allValuesData}
        isLoading={isLoading}
        setIsLoading={setIsLoading}
      />
    </div>
  );
}

export default App;
