import './App.css';
import React, { useState, useRef } from 'react';
import Header from './components/Header/Header'
import Form from './components/Form/Form'
import Responses from './components/Responses/Responses'

const App = () => {

  const [usersPromptData, updateUsersPrompt] = useState([])
  const [allValuesData, updateValuesData] = useState([])
  const [isLoading, setIsLoading] = useState(false);

  return (
    <div className="App">
      <main>
        <Header />
        <Responses
          allValuesData={allValuesData}
          setIsLoading={setIsLoading}
          isLoading={isLoading}
          dummy={dummy}
        />
      </main>
      <Form
        usersPromptData={usersPromptData}
        updateUsersPrompt={updateUsersPrompt}

        updateValuesData={
          (data) => {
            updateValuesData(data);
            document.getElementById('responsesContainer').scrollTop = document.getElementById('responsesContainer').scrollHeight;
          }
        }

        allValuesData={allValuesData}
        isLoading={isLoading}
        setIsLoading={setIsLoading}
      />
    </div>
  );
}

export default App;
